    and OUSHUCOUNT = #{OUSHUCOUNT}

SELECT
*
FROM
reet_tbl a
INNER JOIN (
SELECT
*
FROM
reet_tbl b
INNER JOIN (
SELECT
ROOMID as dROOMID,POINT as dPOINT ,`VALUE` as dVALUE,JISHUCOUNT as dJISHUCOUNT,OUSHUCOUNT as dOUSHUCOUNT
FROM
reet_tbl c

    c.JISHUCOUNT = #{JISHUCOUNT1}
    and c.OUSHUCOUNT = #{OUSHUCOUNT1}
    and c.VALUE = #{VALUE}

) d ON b.ROOMID = d.dROOMID
AND b.POINT = d.dPOINT + 1
AND b.`VALUE` = d.dVALUE

    b.JISHUCOUNT = #{JISHUCOUNT2}
    and b.OUSHUCOUNT = #{OUSHUCOUNT2}
    and b.VALUE = #{VALUE}

) e ON a.POINT = e.POINT + 1
AND a.ROOMID = e.ROOMID ORDER BY a.TIME ASC

SELECT * FROM reet_tbl a
INNER JOIN (SELECT * from room_tbl ORDER BY room_tbl.STRARTTIME ASC LIMIT #{pages},#{allCount}) h ON a.ROOMID = h.ID
INNER JOIN ( SELECT * FROM reet_tbl b INNER JOIN ( SELECT ROOMID AS dROOMID, POINT AS dPOINT, `VALUE` AS dVALUE, JISHUCOUNT AS dJISHUCOUNT, OUSHUCOUNT AS dOUSHUCOUNT FROM reet_tbl c INNER JOIN ( SELECT ROOMID AS dROOMID1, POINT AS dPOINT1, `VALUE` AS dVALUE, JISHUCOUNT AS dJISHUCOUNT1, OUSHUCOUNT AS dOUSHUCOUNT1 FROM reet_tbl f
f.JISHUCOUNT = #{JISHUCOUNT1} and f.OUSHUCOUNT = #{OUSHUCOUNT1} and f.VALUE = #{VALUE1}
) g ON c.ROOMID = g.dROOMID1 AND c.POINT = g.dPOINT1 + 1
c.JISHUCOUNT = #{JISHUCOUNT2} and c.OUSHUCOUNT = #{OUSHUCOUNT2} and c.VALUE = #{VALUE2}
) d ON b.ROOMID = d.dROOMID AND b.POINT = d.dPOINT + 1
b.JISHUCOUNT = #{JISHUCOUNT3} and b.OUSHUCOUNT = #{OUSHUCOUNT3} and b.VALUE = #{VALUE3}
) e ON a.POINT = e.POINT + 1 AND a.ROOMID = e.ROOMID
and a.TRENT = #{TRENT}
ORDER BY a.TIME ASC

