SELECT * FROM reet_tbl a
INNER JOIN (SELECT * from room_tbl ORDER BY room_tbl.STRARTTIME ASC LIMIT #{pages},#{allCount}) h ON a.ROOMID = h.ID
INNER JOIN ( SELECT ROOMID as dROOMID,POINT as dPOINT ,`VALUE` as dVALUE,JISHUCOUNT as dJISHUCOUNT,OUSHUCOUNT as dOUSHUCOUNT FROM reet_tbl c
c.JISHUCOUNT = #{JISHUCOUNT1} and c.OUSHUCOUNT = #{OUSHUCOUNT1} and c.VALUE = #{VALUE1}
) d ON a.ROOMID = d.dROOMID AND a.POINT = d.dPOINT + 1
and a.TRENT = #{TRENT}
ORDER BY a.TIME ASC

SELECT *, count(PAI) AS PAICOUNT  FROM(
        SELECT
            *,
            GROUP_CONCAT( ZHUANG1,":",XIAN1,":",ZHUANG2,":", XIAN2,":",ZHUANG3,":",XIAN3) AS PAI
        FROM
            reet_tbl

            JISHUCOUNT = #{JISHUCOUNT}
            AND OUSHUCOUNT = #{OUSHUCOUNT}
            and TRENT = #{qiang}

        GROUP BY
            ID
    ) as TMP
GROUP BY
    PAI DESC ORDER BY PAICOUNT DESC LIMIT #{start},#{limit}

SELECT *, count(PAI) AS PAICOUNT FROM ( SELECT *, GROUP_CONCAT( ZHUANG1,":",XIAN1,":",ZHUANG2,":", XIAN2,":",ZHUANG3,":",XIAN3) AS PAI FROM reet_tbl
JISHUCOUNT = #{JISHUCOUNT} AND OUSHUCOUNT = #{OUSHUCOUNT} and TRENT = #{qiang}
GROUP BY ID ) as TMP GROUP BY PAI DESC ORDER BY PAICOUNT DESC

